// Toma TODOS los items de entrada (en tu caso 2) y los fusiona en uno.
// Soporta que venga message.content en un item y customer_feedback_str en otro.

const items = $input.all();
const out = {};

// Recorremos y recogemos campos, si existen
for (const it of items) {
  const j = it.json || {};
  if (j.message?.content) out.message_content = j.message.content;
  if (j.finish_reason) out.finish_reason = j.finish_reason;
  if (j.customer_feedback_str) out.customer_feedback_str = j.customer_feedback_str;
  if (j.top3_count !== undefined) out.top3_count = j.top3_count;
}

// Extra: aseguramos que la page NO est√© archivada cuando la actualicemos
out.archived = false;

// Si quer√©s sanear strings (opcional):
// out.message_content = (out.message_content || "").toString();
// out.customer_feedback_str = (out.customer_feedback_str || "").toString();

return [{ json: out }];

// Slack Matches ‚Üí Bulleted summary (with safe fallback)
// Input shape esperado: cada item es un "match" como el que pegaste (con .text, .blocks, .ts, .permalink)

function extractText(row) {
  // 1) usa .text si existe
  if (row?.text) return String(row.text);

  // 2) si no, arma texto desde blocks (rich_text)
  const out = [];
  (function walk(n) {
    if (!n) return;
    if (Array.isArray(n)) return n.forEach(walk);
    if (typeof n === 'object') {
      if (n.text) out.push(String(n.text));
      if (n.elements) walk(n.elements);
      if (n.blocks) walk(n.blocks);
    }
  })(row?.blocks);
  return out.join(' ').trim();
}

try {
  const items = $input.all();
  const rows = (Array.isArray(items) ? items : []).map(i => i.json ?? i).filter(Boolean);

  if (!rows.length) {
    return [{
      json: {
        customer_feedback_str: "No customer feedback",
        top_count: 0,
        status: "empty-input"
      }
    }];
  }

  // Limpia, extrae texto y ordena por ts desc
  const cleaned = rows.map(r => {
    const text = extractText(r).replace(/\s+/g, ' ').trim();
    const tsNum = Number(r.ts || 0) || 0;
    const link = r.permalink || '';
    return { text, ts: tsNum, link };
  }).filter(x => x.text);

  if (!cleaned.length) {
    return [{
      json: {
        customer_feedback_str: "No customer feedback",
        top_count: 0,
        status: "no-text-after-extract"
      }
    }];
  }

  cleaned.sort((a, b) => b.ts - a.ts);

  // Arm√° bullets (pod√©s ajustar el top N)
  const TOP_N = 5;
  const bullets = cleaned.slice(0, TOP_N)
    .map(x => x.link ? `‚Ä¢ ${x.text}  (${x.link})` : `‚Ä¢ ${x.text}`)
    .join('\n')
    .slice(0, 1999); // cap por si va a Notion

  return [{
    json: {
      customer_feedback_str: bullets,
      top_count: Math.min(TOP_N, cleaned.length),
      status: "ok"
    }
  }];

} catch (err) {
  return [{
    json: {
      customer_feedback_str: "No customer feedback",
      top_count: 0,
      status: "exception",
      error_message: String(err?.message || "unknown")
    }
  }];
}
